import * as React from "react";
import { Pressable, Text, View } from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";
import { Colors } from "../../../Utils/index";
import { styles } from "./styles";
Icon.loadFont();
export const SelectContainer = ({ arrowColor = Colors.black, arrowSize = 30, handleDropDownPress, selectedItem, itemLabel, }) => {
    return (<Pressable style={styles.pressable} onPress={handleDropDownPress}>
      {selectedItem && (<View style={styles.textContainer}>
          <Text numberOfLines={1}>{selectedItem[itemLabel]}</Text>
        </View>)}
      <View style={[styles.iconContainer, !selectedItem && { flex: 1 }]}>
        <Icon name="caret-down" size={arrowSize} color={arrowColor}/>
      </View>
    </Pressable>);
};
