import * as React from "react";
import { Pressable, Text } from "react-native";
import { styles as stylesWrapper } from "./styles";
export const SelectItem = ({ id, label, fontStyle, containerStyle, containerBackgroundColor, handlePress, }) => {
    const styles = stylesWrapper(containerBackgroundColor);
    return (<Pressable style={[styles.container, containerStyle]} key={`${id}`} onPress={() => handlePress(id)}>
      <Text numberOfLines={1} style={[styles.text, fontStyle]}>
        {label}
      </Text>
    </Pressable>);
};
